function addNewForm() {
  // 1. Get references to relevant widgets using the widget tree
  const originalForm = appsmith.workflows.getById("Form3Copy"); // Replace with your form ID
  const container = appsmith.workflows.getById("Container3Copy"); // Replace with your container ID

  // 2. Clone the original form
  const newForm = originalForm.cloneNode(true);

  // 3. Clear the data of the new form (optional)
  newForm.querySelectorAll("input[type='text'], textarea").forEach(input => input.value = "");
  newForm.querySelectorAll("select").forEach(select => select.value = ""); // Adjust for other input types

  // 4. Generate a unique ID for the new form (optional)
  const uniqueId = Math.random().toString(36).substring(2, 7); // Example ID format
  newForm.id = `clonedForm-${uniqueId}`; // Set a unique ID

  // 5. Create and add a "Remove" button to the new form
  const removeButton = document.createElement("button");
  removeButton.textContent = "Remove";
  removeButton.addEventListener("click", () => removeForm(newForm.id));
  newForm.appendChild(removeButton);

  // 6. Append the new form to the container
  container.appendChild(newForm);
}
function removeForm(formId) {
  const container = document.getElementById("Container1Copy"); // Replace with your container ID
  const formToRemove = container.querySelector(`#${formId}`);

  if (formToRemove) {
    container.removeChild(formToRemove);
  } else {
    console.error("Form with ID", formId, "not found for removal.");
  }
}

